import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Paper,
  Typography,
  Grid,
  Card,
  CardContent,
  CardActions,
  Button,
  Alert,
  CircularProgress,
  Box
} from '@mui/material';
import { roomService } from '../../services/room.service';
import { Room } from '../../types/room';

export const AvailableRooms: React.FC = () => {
  const navigate = useNavigate();
  const [rooms, setRooms] = useState<Room[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchAvailableRooms();
  }, []);

  const fetchAvailableRooms = async () => {
    try {
      const allRooms = await roomService.getAllRooms();
      const availableRooms = allRooms.filter((room: Room) => 
        room.status.toLowerCase() === 'available' && 
        room.currentOccupancy < room.capacity
      );
      setRooms(availableRooms);
    } catch (err: any) {
      setError('Không thể tải danh sách phòng');
    } finally {
      setLoading(false);
    }
  };

  const handleRegisterRoom = (roomId: number) => {
    navigate(`/student/room-registration/${roomId}`);
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="60vh">
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Container maxWidth="lg">
      <Paper sx={{ p: 4, mt: 4 }}>
        <Typography variant="h4" gutterBottom>
          Danh sách phòng có sẵn
        </Typography>

        {error && (
          <Alert severity="error" sx={{ mb: 2 }}>
            {error}
          </Alert>
        )}

        <Grid container spacing={3}>
          {rooms.map((room) => (
            <Grid item xs={12} sm={6} md={4} key={room.id}>
              <Card>
                <CardContent>
                  <Typography variant="h6" gutterBottom>
                    Phòng {room.roomNumber}
                  </Typography>
                  <Typography color="textSecondary" gutterBottom>
                    Tòa {room.building} - Tầng {room.floor}
                  </Typography>
                  <Typography variant="body2">
                    Loại phòng: {room.type}
                  </Typography>
                  <Typography variant="body2">
                    Giá phòng: {room.price.toLocaleString('vi-VN')} VNĐ/tháng
                  </Typography>
                  <Typography variant="body2">
                    Sức chứa: {room.currentOccupancy}/{room.capacity}
                  </Typography>
                </CardContent>
                <CardActions>
                  <Button 
                    size="small" 
                    color="primary"
                    onClick={() => handleRegisterRoom(room.id)}
                  >
                    Đăng ký
                  </Button>
                </CardActions>
              </Card>
            </Grid>
          ))}
          {rooms.length === 0 && (
            <Grid item xs={12}>
              <Alert severity="info">
                Hiện không có phòng nào khả dụng
              </Alert>
            </Grid>
          )}
        </Grid>
      </Paper>
    </Container>
  );
};
