import React, { useState } from 'react';
import {
  Box,
  Container,
  Typography,
  TextField,
  Button,
  Grid,
  Paper,
  Alert,
  MenuItem
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { roomService } from '../../services/room.service';

interface RoomForm {
  roomNumber: string;
  building: string;
  floor: string;
  capacity: string;
  type: string;
  price: string;
  status: string;
}

export const RoomRegistration: React.FC = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState<RoomForm>({
    roomNumber: '',
    building: '',
    floor: '',
    capacity: '',
    type: '',
    price: '',
    status: 'available'
  });
  const [error, setError] = useState<string>('');
  const [success, setSuccess] = useState<string>('');
  const [isSubmitting, setIsSubmitting] = useState<boolean>(false);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsSubmitting(true);
    setError('');
    
    try {
      // Tạo object dữ liệu để gửi lên server
      const roomData = {
        roomNumber: formData.roomNumber,
        building: formData.building,
        floor: parseInt(formData.floor),
        capacity: parseInt(formData.capacity),
        type: formData.type,
        price: parseFloat(formData.price),
        status: formData.status
      };
      
      // Gọi API đăng ký phòng
      await roomService.registerRoom(roomData);
      
      setSuccess('Đăng ký phòng thành công!');
      setTimeout(() => {
        navigate('/admin');
      }, 3000);
    } catch (err: any) {
      setError(err.message || 'Có lỗi xảy ra khi đăng ký phòng');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <Container maxWidth="md">
      <Box sx={{ mt: 4, mb: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom align="center">
          Đăng ký phòng ký túc xá
        </Typography>
        <Paper sx={{ p: 3, mt: 2 }}>
          <form onSubmit={handleSubmit}>
            <Grid container spacing={3}>
              {error && (
                <Grid item xs={12}>
                  <Alert severity="error">{error}</Alert>
                </Grid>
              )}
              {success && (
                <Grid item xs={12}>
                  <Alert severity="success">{success}</Alert>
                </Grid>
              )}
              <Grid item xs={12} sm={6}>
                <TextField
                  required
                  fullWidth
                  label="Số phòng"
                  name="roomNumber"
                  value={formData.roomNumber}
                  onChange={handleChange}
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  required
                  fullWidth
                  label="Tòa nhà"
                  name="building"
                  value={formData.building}
                  onChange={handleChange}
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  required
                  fullWidth
                  label="Tầng"
                  name="floor"
                  type="number"
                  value={formData.floor}
                  onChange={handleChange}
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  required
                  fullWidth
                  label="Sức chứa"
                  name="capacity"
                  type="number"
                  value={formData.capacity}
                  onChange={handleChange}
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  required
                  fullWidth
                  select
                  label="Loại phòng"
                  name="type"
                  value={formData.type}
                  onChange={handleChange}
                >
                  <MenuItem value="standard">Tiêu chuẩn</MenuItem>
                  <MenuItem value="deluxe">Cao cấp</MenuItem>
                  <MenuItem value="suite">Suite</MenuItem>
                </TextField>
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  required
                  fullWidth
                  label="Giá phòng"
                  name="price"
                  type="number"
                  value={formData.price}
                  onChange={handleChange}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  select
                  label="Trạng thái"
                  name="status"
                  value={formData.status}
                  onChange={handleChange}
                >
                  <MenuItem value="available">Còn trống</MenuItem>
                  <MenuItem value="occupied">Đã có người ở</MenuItem>
                  <MenuItem value="maintenance">Đang bảo trì</MenuItem>
                </TextField>
              </Grid>
              <Grid item xs={12}>
                <Box sx={{ display: 'flex', gap: 2, justifyContent: 'flex-end' }}>
                  <Button
                    variant="outlined"
                    onClick={() => navigate('/admin')}
                    disabled={isSubmitting}
                  >
                    Quay lại
                  </Button>
                  <Button
                    type="submit"
                    variant="contained"
                    disabled={isSubmitting}
                  >
                    {isSubmitting ? 'Đang xử lý...' : 'Đăng ký'}
                  </Button>
                </Box>
              </Grid>
            </Grid>
          </form>
        </Paper>
      </Box>
    </Container>
  );
};
