import React, { useState, useEffect } from 'react';
import {
  Container,
  Paper,
  Typography,
  Box,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Button,
  Chip,
  CircularProgress,
  Alert,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Grid
} from '@mui/material';
import { format } from 'date-fns';
import { vi } from 'date-fns/locale';
import { paymentService } from '../../services/payment.service';
import { studentService } from '../../services/student.service';
import { toast } from 'sonner';
import { useToast } from "@/components/ui/use-toast";
import {
  TableHeader,
} from "@/components/ui/table";

interface Payment {
  id: number;
  studentId: number;
  amount: string;
  paymentDate: string;
  evidenceImage: string;
  status: 'pending' | 'approved' | 'rejected';
  month: number;
  year: number;
  rejectionReason: string | null;
  student: {
    id: number;
    studentId: string;
    fullName: string;
  };
}

const PaymentManagement = () => {
  const [payments, setPayments] = useState<Payment[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [selectedPayment, setSelectedPayment] = useState<Payment | null>(null);
  const [openDialog, setOpenDialog] = useState(false);
  const [rejectionReason, setRejectionReason] = useState('');
  const { toast } = useToast();

  useEffect(() => {
    fetchPayments();
  }, []);

  const fetchPayments = async () => {
    try {
      const response = await paymentService.getAllPayments();
      if (response.success) {
        setPayments(response.data);
      }
    } catch (error) {
      console.error("Error fetching payments:", error);
      toast({
        variant: "destructive",
        title: "Lỗi",
        description: "Không thể tải danh sách thanh toán",
      });
    } finally {
      setLoading(false);
    }
  };

  const handleApprove = async (id: number) => {
    try {
      await paymentService.approvePayment(id);
      toast({
        title: "Thành công",
        description: "Đã phê duyệt thanh toán",
      });
      fetchPayments();
    } catch (error) {
      console.error("Error approving payment:", error);
      toast({
        variant: "destructive",
        title: "Lỗi",
        description: "Không thể phê duyệt thanh toán",
      });
    }
  };

  const handleReject = async (id: number) => {
    const reason = prompt("Nhập lý do từ chối:");
    if (reason) {
      try {
        await paymentService.rejectPayment(id, reason);
        toast({
          title: "Thành công",
          description: "Đã từ chối thanh toán",
        });
        fetchPayments();
      } catch (error) {
        console.error("Error rejecting payment:", error);
        toast({
          variant: "destructive",
          title: "Lỗi",
          description: "Không thể từ chối thanh toán",
        });
      }
    }
  };

  const openRejectDialog = (payment: Payment) => {
    setSelectedPayment(payment);
    setOpenDialog(true);
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'approved':
        return 'text-green-600';
      case 'rejected':
        return 'text-red-600';
      default:
        return 'text-yellow-600';
    }
  };

  const formatCurrency = (amount: string) => {
    return new Intl.NumberFormat("vi-VN", {
      style: "currency",
      currency: "VND",
    }).format(Number(amount));
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="60vh">
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Container maxWidth="lg">
      <Box sx={{ mt: 4, mb: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom align="center">
          Quản lý thanh toán
        </Typography>
        
        {error && (
          <Alert severity="error" sx={{ mb: 3 }}>
            {error}
          </Alert>
        )}
        
        {success && (
          <Alert severity="success" sx={{ mb: 3 }}>
            {success}
          </Alert>
        )}
        
        <Paper sx={{ width: '100%', mb: 2 }}>
          <TableContainer>
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Mã sinh viên</TableHead>
                  <TableHead>Tên sinh viên</TableHead>
                  <TableHead>Số tiền</TableHead>
                  <TableHead>Tháng/Năm</TableHead>
                  <TableHead>Ngày thanh toán</TableHead>
                  <TableHead>Trạng thái</TableHead>
                  <TableHead>Thao tác</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {payments.map((payment) => (
                  <TableRow key={payment.id}>
                    <TableCell>{payment.student.studentId}</TableCell>
                    <TableCell>{payment.student.fullName}</TableCell>
                    <TableCell>{formatCurrency(payment.amount)}</TableCell>
                    <TableCell>
                      {payment.month}/{payment.year}
                    </TableCell>
                    <TableCell>
                      {format(new Date(payment.paymentDate), "dd/MM/yyyy HH:mm", {
                        locale: vi,
                      })}
                    </TableCell>
                    <TableCell className={getStatusColor(payment.status)}>
                      {payment.status === "approved"
                        ? "Đã phê duyệt"
                        : payment.status === "rejected"
                        ? "Đã từ chối"
                        : "Chờ phê duyệt"}
                    </TableCell>
                    <TableCell>
                      {payment.status === "pending" && (
                        <div className="space-x-2">
                          <Button
                            variant="outlined"
                            size="small"
                            onClick={() => handleApprove(payment.id)}
                          >
                            Phê duyệt
                          </Button>
                          <Button
                            variant="outlined"
                            size="small"
                            onClick={() => handleReject(payment.id)}
                          >
                            Từ chối
                          </Button>
                        </div>
                      )}
                      {payment.status === "rejected" && payment.rejectionReason && (
                        <div className="text-sm text-red-600">
                          Lý do: {payment.rejectionReason}
                        </div>
                      )}
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </Paper>
      </Box>

      {/* Dialog từ chối thanh toán */}
      <Dialog open={openDialog} onClose={() => setOpenDialog(false)}>
        <DialogTitle>Từ chối thanh toán</DialogTitle>
        <DialogContent>
          <Grid container spacing={2} sx={{ mt: 1 }}>
            <Grid item xs={12}>
              <Typography variant="body1" gutterBottom>
                Sinh viên: {selectedPayment?.student?.fullName}
              </Typography>
              <Typography variant="body1" gutterBottom>
                Số tiền: {selectedPayment?.amount.toLocaleString('vi-VN')} VNĐ
              </Typography>
              <Typography variant="body1" gutterBottom>
                Tháng/Năm: {selectedPayment?.month}/{selectedPayment?.year}
              </Typography>
            </Grid>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Lý do từ chối"
                multiline
                rows={4}
                value={rejectionReason}
                onChange={(e) => setRejectionReason(e.target.value)}
                required
              />
            </Grid>
          </Grid>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenDialog(false)}>Hủy</Button>
          <Button 
            onClick={handleReject} 
            color="error" 
            variant="contained"
            disabled={!rejectionReason.trim()}
          >
            Từ chối
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default PaymentManagement;
