import React, { useState, useEffect } from 'react';
import {
  Box,
  Container,
  Typography,
  Paper,
  Grid,
  Card,
  CardContent,
  CardActions,
  Button,
  List,
  ListItem,
  ListItemText,
  Divider,
  Chip,
  Alert,
  CircularProgress,
  IconButton
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { roomService } from '../../services/room.service';
import { roomAssignmentService } from '../../services/roomAssignment.service';
import { notificationService, UserNotification } from '../../services/notification.service';
import DeleteIcon from '@mui/icons-material/Delete';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';

interface Room {
  id: number;
  roomNumber: string;
  building: string;
  floor: number;
  capacity: number;
  currentOccupancy: number;
  type: string;
  price: number;
  status: string;
}

interface RoomAssignment {
  id: number;
  roomId: number;
  studentId: number;
  startDate: string;
  endDate: string;
  status: string;
  room: Room;
}

export const UserHome: React.FC = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [currentRoom, setCurrentRoom] = useState<RoomAssignment | null>(null);
  const [availableRooms, setAvailableRooms] = useState<Room[]>([]);
  const [notifications, setNotifications] = useState<UserNotification[]>([]);

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('vi-VN');
  };

  const formatDateTime = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleString('vi-VN');
  };

  useEffect(() => {
    fetchUserData();
  }, []);

  const fetchUserData = async () => {
    try {
      setLoading(true);
      // Fetch current room assignment
      const assignments = await roomAssignmentService.getRoomAssignments();
      const currentAssignment = assignments.find((a: RoomAssignment) => a.status === 'active');
      if (currentAssignment) {
        setCurrentRoom(currentAssignment);
      }

      // Fetch available rooms
      const rooms = await roomService.getAllRooms();
      const available = rooms.filter((room: Room) => 
        room.status.toLowerCase() === 'available' && 
        room.currentOccupancy < room.capacity
      );
      setAvailableRooms(available);

      // Fetch notifications
      const fetchedNotifications = await notificationService.getNotifications();
      setNotifications(fetchedNotifications);

      setLoading(false);
    } catch (err: any) {
      setError(err.message || 'Không thể tải dữ liệu');
      setLoading(false);
    }
  };

  const handleRegisterRoom = (roomId: number) => {
    navigate(`/user/room-registration/${roomId}`);
  };

  const handleMarkAsRead = async (notificationId: number) => {
    try {
      await notificationService.markAsRead(notificationId);
      setNotifications(notifications.map(notification => 
        notification.id === notificationId 
          ? { ...notification, isRead: true } 
          : notification
      ));
    } catch (err: any) {
      console.error('Không thể đánh dấu thông báo đã đọc:', err);
    }
  };

  const handleDeleteNotification = async (notificationId: number) => {
    try {
      await notificationService.deleteNotification(notificationId);
      setNotifications(notifications.filter(notification => notification.id !== notificationId));
    } catch (err: any) {
      console.error('Không thể xóa thông báo:', err);
    }
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="60vh">
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Container maxWidth="lg">
        <Alert severity="error" sx={{ mt: 4 }}>{error}</Alert>
      </Container>
    );
  }

  return (
    <Container maxWidth="lg">
      <Box sx={{ mt: 4, mb: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          Trang chủ
        </Typography>

        <Grid container spacing={3}>
          {/* Current Room Information */}
          <Grid item xs={12} md={6}>
            <Paper sx={{ p: 3 }}>
              <Typography variant="h6" gutterBottom>
                Thông tin phòng hiện tại
              </Typography>
              {currentRoom ? (
                <Card>
                  <CardContent>
                    <Typography variant="h5" gutterBottom>
                      Phòng {currentRoom.room.roomNumber}
                    </Typography>
                    <Typography color="textSecondary" gutterBottom>
                      Tòa {currentRoom.room.building} - Tầng {currentRoom.room.floor}
                    </Typography>
                    <Typography variant="body2">
                      Loại phòng: {currentRoom.room.type}
                    </Typography>
                    <Typography variant="body2">
                      Giá phòng: {currentRoom.room.price.toLocaleString('vi-VN')} VNĐ/tháng
                    </Typography>
                    <Typography variant="body2">
                      Ngày bắt đầu: {formatDate(currentRoom.startDate)}
                    </Typography>
                    <Typography variant="body2">
                      Ngày kết thúc: {formatDate(currentRoom.endDate)}
                    </Typography>
                  </CardContent>
                </Card>
              ) : (
                <Alert severity="info">
                  Bạn chưa đăng ký phòng nào. Hãy xem danh sách phòng có sẵn bên dưới.
                </Alert>
              )}
            </Paper>
          </Grid>

          {/* Available Rooms */}
          <Grid item xs={12} md={6}>
            <Paper sx={{ p: 3 }}>
              <Typography variant="h6" gutterBottom>
                Phòng có thể đăng ký
              </Typography>
              <List>
                {availableRooms.map((room) => (
                  <React.Fragment key={room.id}>
                    <ListItem>
                      <ListItemText
                        primary={`Phòng ${room.roomNumber} - Tòa ${room.building}`}
                        secondary={
                          <>
                            <Typography component="span" variant="body2" color="text.primary">
                              {room.type} - {room.price.toLocaleString('vi-VN')} VNĐ/tháng
                            </Typography>
                            <br />
                            <Typography component="span" variant="body2">
                              Sức chứa: {room.currentOccupancy}/{room.capacity}
                            </Typography>
                          </>
                        }
                      />
                      <Button
                        variant="contained"
                        color="primary"
                        onClick={() => handleRegisterRoom(room.id)}
                      >
                        Đăng ký
                      </Button>
                    </ListItem>
                    <Divider />
                  </React.Fragment>
                ))}
                {availableRooms.length === 0 && (
                  <ListItem>
                    <ListItemText primary="Hiện không có phòng nào khả dụng" />
                  </ListItem>
                )}
              </List>
            </Paper>
          </Grid>

          {/* Notifications */}
          <Grid item xs={12}>
            <Paper sx={{ p: 3 }}>
              <Box display="flex" justifyContent="space-between" alignItems="center" mb={2}>
                <Typography variant="h6">
                  Thông báo
                </Typography>
                {notifications.some(n => !n.isRead) && (
                  <Button
                    variant="outlined"
                    size="small"
                    onClick={() => notificationService.markAllAsRead()}
                  >
                    Đánh dấu tất cả đã đọc
                  </Button>
                )}
              </Box>
              <List>
                {notifications.map((notification) => (
                  <React.Fragment key={notification.id}>
                    <ListItem
                      secondaryAction={
                        <Box>
                          {!notification.isRead && (
                            <IconButton
                              edge="end"
                              aria-label="mark as read"
                              onClick={() => handleMarkAsRead(notification.id)}
                              sx={{ mr: 1 }}
                            >
                              <CheckCircleIcon />
                            </IconButton>
                          )}
                          <IconButton
                            edge="end"
                            aria-label="delete"
                            onClick={() => handleDeleteNotification(notification.id)}
                          >
                            <DeleteIcon />
                          </IconButton>
                        </Box>
                      }
                    >
                      <ListItemText
                        primary={
                          <Box display="flex" alignItems="center" gap={1}>
                            <Typography variant="subtitle1">
                              {notification.title}
                            </Typography>
                            {!notification.isRead && (
                              <Chip
                                label="Mới"
                                color="primary"
                                size="small"
                              />
                            )}
                          </Box>
                        }
                        secondary={
                          <>
                            <Typography variant="body2" component="span">
                              {notification.content}
                            </Typography>
                            <br />
                            <Typography variant="caption" color="textSecondary">
                              {formatDateTime(notification.createdAt)}
                            </Typography>
                          </>
                        }
                      />
                    </ListItem>
                    <Divider />
                  </React.Fragment>
                ))}
                {notifications.length === 0 && (
                  <ListItem>
                    <ListItemText primary="Không có thông báo nào" />
                  </ListItem>
                )}
              </List>
            </Paper>
          </Grid>
        </Grid>
      </Box>
    </Container>
  );
};
