import React, { useState, useEffect } from "react";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Button } from "@/components/ui/button";
import { paymentService } from "@/services/payment.service";
import { format } from "date-fns";
import { vi } from "date-fns/locale";
import { useToast } from "@/components/ui/use-toast";

interface Payment {
  id: number;
  studentId: number;
  amount: string;
  paymentDate: string;
  evidenceImage: string;
  status: "pending" | "approved" | "rejected";
  month: number;
  year: number;
  rejectionReason: string | null;
  student: {
    id: number;
    studentId: string;
    fullName: string;
  };
}

interface ApiResponse<T> {
  success: boolean;
  data: T;
  message?: string;
}

const PaymentManagement = () => {
  const [payments, setPayments] = useState<Payment[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [selectedPayment, setSelectedPayment] = useState<Payment | null>(null);
  const [openDialog, setOpenDialog] = useState(false);
  const [rejectionReason, setRejectionReason] = useState('');
  const { toast } = useToast();

  useEffect(() => {
    fetchPayments();
  }, []);

  const fetchPayments = async () => {
    try {
      const response = await paymentService.getAllPayments();
      if (response.data) {
        setPayments(response.data);
      }
    } catch (error) {
      console.error("Error fetching payments:", error);
      toast({
        variant: "destructive",
        title: "Lỗi",
        description: "Không thể tải danh sách thanh toán",
      });
    } finally {
      setLoading(false);
    }
  };

  const handleApprove = async (id: number) => {
    try {
      await paymentService.approvePayment(id);
      toast({
        title: "Thành công",
        description: "Đã phê duyệt thanh toán",
      });
      fetchPayments();
    } catch (error) {
      console.error("Error approving payment:", error);
      toast({
        variant: "destructive",
        title: "Lỗi",
        description: "Không thể phê duyệt thanh toán",
      });
    }
  };

  const handleReject = async (id: number) => {
    const reason = prompt("Nhập lý do từ chối:");
    if (reason) {
      try {
        await paymentService.rejectPayment(id, reason);
        toast({
          title: "Thành công",
          description: "Đã từ chối thanh toán",
        });
        fetchPayments();
      } catch (error) {
        console.error("Error rejecting payment:", error);
        toast({
          variant: "destructive",
          title: "Lỗi",
          description: "Không thể từ chối thanh toán",
        });
      }
    }
  };

  const openRejectDialog = (payment: Payment) => {
    setSelectedPayment(payment);
    setOpenDialog(true);
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case "approved":
        return "text-green-600";
      case "rejected":
        return "text-red-600";
      default:
        return "text-yellow-600";
    }
  };

  const formatCurrency = (amount: string) => {
    return new Intl.NumberFormat("vi-VN", {
      style: "currency",
      currency: "VND",
    }).format(Number(amount));
  };

  if (loading) {
    return <div>Đang tải...</div>;
  }

  return (
    <div className="container mx-auto py-6">
      <h1 className="text-2xl font-bold mb-6">Quản lý thanh toán</h1>
      <div className="rounded-md border">
        <Table>
          <TableHeader>
            <TableRow>
              <TableHead>Mã sinh viên</TableHead>
              <TableHead>Tên sinh viên</TableHead>
              <TableHead>Số tiền</TableHead>
              <TableHead>Tháng/Năm</TableHead>
              <TableHead>Ngày thanh toán</TableHead>
              <TableHead>Trạng thái</TableHead>
              <TableHead>Thao tác</TableHead>
            </TableRow>
          </TableHeader>
          <TableBody>
            {payments.map((payment) => (
              <TableRow key={payment.id}>
                <TableCell>{payment.student.studentId}</TableCell>
                <TableCell>{payment.student.fullName}</TableCell>
                <TableCell>{formatCurrency(payment.amount)}</TableCell>
                <TableCell>
                  {payment.month}/{payment.year}
                </TableCell>
                <TableCell>
                  {format(new Date(payment.paymentDate), "dd/MM/yyyy HH:mm", {
                    locale: vi,
                  })}
                </TableCell>
                <TableCell className={getStatusColor(payment.status)}>
                  {payment.status === "approved"
                    ? "Đã phê duyệt"
                    : payment.status === "rejected"
                    ? "Đã từ chối"
                    : "Chờ phê duyệt"}
                </TableCell>
                <TableCell>
                  {payment.status === "pending" && (
                    <div className="space-x-2">
                      <Button
                        variant="outlined"
                        size="small"
                        onClick={() => handleApprove(payment.id)}
                      >
                        Phê duyệt
                      </Button>
                      <Button
                        variant="outlined"
                        size="small"
                        onClick={() => handleReject(payment.id)}
                      >
                        Từ chối
                      </Button>
                    </div>
                  )}
                  {payment.status === "rejected" && payment.rejectionReason && (
                    <div className="text-sm text-red-600">
                      Lý do: {payment.rejectionReason}
                    </div>
                  )}
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </div>
    </div>
  );
};

export default PaymentManagement;
